import { Component, ViewChild } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { MessageService } from '../../service/message.service';
import { BehaviorSubject } from 'rxjs';

export interface Message {
  type: string;
  message: string;
}

export interface GeneratedQuestion {
  questions: string;
}

@Component({
  selector: 'app-help-support',
  templateUrl: './help-support.component.html',
  styleUrls: ['./help-support.component.scss'],
})
export class HelpSupportComponent {
  isOpen = false;
  loading = false;
  messages: Message[] = [];
  state: 'userType' | 'botType' | 'generatedQuestions' = 'botType'; // Initial state set to 'botType'
  messages$ = new BehaviorSubject<Array<{ type: string, message: string }>>([]);
  chatForm = new FormGroup({
    message: new FormControl('', [Validators.required]),
  });
  @ViewChild('scrollMe') private myScrollContainer: any;
  generatedQuestions: GeneratedQuestion[] = [];
  lastReceivedAnswer: any;

  constructor(private messageService: MessageService) {
    this.messages.push({
      type: 'client',
      message: 'Hi, I am your support agent. How can I help you?',
    });
  }

  openSupportPopup() {
    this.isOpen = !this.isOpen;
  }

  sendMessage() {
    const sentMessage = this.chatForm.value.message!;
    console.log('sentMessage=' + sentMessage);
    this.loading = true;
    this.messages.push({
      type: 'user',
      message: sentMessage,
    });
    this.messages$.next(this.messages$.getValue().concat([{ type: 'user', message: sentMessage }]));
    this.chatForm.reset();
    this.scrollToBottom();

    this.messageService.sendMessage(sentMessage).subscribe((response: any) => {
      this.loading = false;
      this.state = 'generatedQuestions'; // Set state to 'generatedQuestions' after getting response
      this.lastReceivedAnswer = response.answer;
      this.messages.push({
        type: 'client',
        message: this.lastReceivedAnswer,
      });
      this.messages$.next(this.messages$.getValue().concat([{ type: 'client', message: this.lastReceivedAnswer }]));
      console.log('response.answer:', this.lastReceivedAnswer);
      this.scrollToBottom();
    });
  }

  askMoreQuestions() {
    this.state = 'generatedQuestions';
    this.generatedQuestions = [];
    if(this.lastReceivedAnswer) {
      this.messageService.questionGeneration(this.lastReceivedAnswer).subscribe((qResponse: any) => {
        console.log('this.generatedQuestions:', qResponse.related_questions.generated_questions);
        const questionsObj = qResponse.related_questions.generated_questions;
        console.log('questionsObj:', questionsObj);
        if (questionsObj) {
          const questions = questionsObj[0].questions;
          console.log('questions=' + questions);
          questions.slice(0, 4).forEach((question: string) => {
            this.generatedQuestions.push({
              questions: question,
            });
          });
        }
        console.log('this.questions:', this.generatedQuestions);
        console.log('this.messages:', this.messages);
      });
    }
  }

  populateInput(question: string) {
    this.chatForm.get('message')!.setValue(question);
    this.sendMessage();
    this.state = 'botType'; // Set state to 'botType' after bot responds to the selected question
  }

  scrollToBottom() {
    setTimeout(() => {
      try {
        this.myScrollContainer.nativeElement.scrollTop = this.myScrollContainer.nativeElement.scrollHeight + 500;
      } catch (err) {}
    }, 150);
  }
}
<div id="helpdesk">
  <button id="helpdesk-popup-button" (click)="openSupportPopup()">
    Need Help?
  </button>
  <div id="helpdesk-popup" [style.display]="isOpen ? 'block' : 'none'">
    <div id="helpdesk-popup-header">
      Chatbot Support!
      <button id="helpdesk-popup-close-button" (click)="openSupportPopup()">
        Close
      </button>
    </div>
    <div id="helpdesk-popup-body">
      <div class="messages" #scrollMe>
        <div *ngFor="let message of messages$ | async" class="message">
          <div [class]="message.type">
            {{ message.message }}
          </div>
        </div>
        <div *ngIf="loading" class="message" style="width: 100%; display: block">
          <div [class]="'client'">...</div>
        </div>
        <div class="messages" *ngIf="state === 'generatedQuestions'">
          <button class="ask-more-button" (click)="askMoreQuestions()">❓ Ask more</button>
          <div *ngFor="let question of generatedQuestions.slice(0, 4)">
            <button class="generate-questions" (click)="populateInput(question.questions)">{{ question.questions }}</button>
          </div>
        </div>
        <div class="message" *ngIf="state === 'generatedQuestions'">
          <div [class]="'client'">Want to ask something else! Type your question below ⬇</div>
        </div>        
      </div>
    </div>
    <form id="helpdesk-popup-footer" [formGroup]="chatForm">
      <input
        formControlName="message"
        type="text"
        id="helpdesk-popup-input"
        placeholder="Type your message here..."
      />
      <button
        id="helpdesk-popup-submit-button"
        [disabled]="!chatForm.valid"
        (click)="sendMessage()"
      >
        Send
      </button>
    </form>
  </div>
</div>
