import { Component, ViewChild } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { MessageService } from '../../service/message.service';
import { BehaviorSubject } from 'rxjs';

export interface Message {
  type: string;
  message: string;
}

export interface GeneratedQuestion {
  questions: string;
}

@Component({
  selector: 'app-help-support',
  templateUrl: './help-support.component.html',
  styleUrls: ['./help-support.component.scss'],
})
export class HelpSupportComponent {
  isOpen = false;
  loading = false;
  messages: Message[] = [];
  // state: 'userType' | 'botType' | 'generatedQuestions' = 'userType';
  messages$ = new BehaviorSubject<Array<{ type: string, message: string }>>([]);
  chatForm = new FormGroup({
    message: new FormControl('', [Validators.required]),
  });
  @ViewChild('scrollMe') private myScrollContainer: any;
  generatedQuestions: GeneratedQuestion[] = [];
 // Create a BehaviorSubject for state management.
 private stateSubject: BehaviorSubject<string> = new BehaviorSubject('userType');
 state$ = this.stateSubject.asObservable();

  constructor(private messageService: MessageService) {
    this.messages.push({
      type: 'client',
      message: 'Hi, I am your support agent. How can I help you?',
    });
  }

  openSupportPopup() {
    this.isOpen = !this.isOpen;
  }

  // Keep track of the last received answer
lastReceivedAnswer: string = '';
// Update state
updateState(newState: string) {
  this.stateSubject.next(newState);
}

sendMessage() {
  // Switch to 'generatedQuestions' state
  this.state = 'userType';
  const sentMessage = this.chatForm.value.message!;
  console.log('sentMessage=' + sentMessage);
  this.loading = true;
  this.messages.push({
    type: 'user',
    message: sentMessage,
  });
  this.messages$.next(this.messages$.getValue().concat([{ type: 'user', message: sentMessage }]));
  this.chatForm.reset();
  this.scrollToBottom();

  // First send the user message and get a response
  this.messageService.sendMessage(sentMessage).subscribe((response: any) => {
    this.loading = false;
    this.lastReceivedAnswer = response.answer; // Save the last received answer

    this.messages.push({
      type: 'client',
      message: this.lastReceivedAnswer,
    });
    this.messages$.next(this.messages$.getValue().concat([{ type: 'client', message: this.lastReceivedAnswer }]));
    this.state = 'userType';

    console.log('response.answer:', this.lastReceivedAnswer);
    this.scrollToBottom();


  });
}

askMoreQuestions() {
  this.state = 'generatedQuestions';
  this.generatedQuestions = []; // Clear the old questions
  if(this.lastReceivedAnswer) {
    // Use the last received answer to generate related questions
    this.messageService.questionGeneration(this.lastReceivedAnswer).subscribe((qResponse: any) => {
      console.log('this.generatedQuestions:', qResponse.related_questions.generated_questions);
      const questionsObj = qResponse.related_questions.generated_questions;
      console.log('questionsObj:', questionsObj);

      if (questionsObj) {
        const questions = questionsObj[0].questions;
        console.log('questions=' + questions);

        // Iterate over the questions and add them to the messages array
        questions.slice(0, 4).forEach((question: string) => {
          this.generatedQuestions.push({
            questions: question,
          });
        });
      }

      console.log('this.questions:', this.generatedQuestions);
      console.log('this.messages:', this.messages);
    });
  }
}
populateInput(question: string) {
  this.chatForm.get('message')!.setValue(question);
    this.sendMessage();  // Automatically send the question after the user selects it
    this.state = 'botType';
}



