import { Component, ViewChild } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { MessageService } from '../../service/message.service';

export interface Message {
  type: string;
  message: string;
}

export interface GeneratedQuestion {
  document_id: string;
  document_sample: string;
  questions: string[];
}

@Component({
  selector: 'app-help-support',
  templateUrl: './help-support.component.html',
  styleUrls: ['./help-support.component.scss'],
})
export class HelpSupportComponent {
  isOpen = false;
  loading = false;
  messages: Message[] = [];
  chatForm = new FormGroup({
    message: new FormControl('', [Validators.required]),
  });
  @ViewChild('scrollMe') private myScrollContainer: any;
  generatedQuestions: GeneratedQuestion[] = [];
  currentDocumentId: string | undefined = '';

  constructor(private messageService: MessageService) {
    this.messages.push({
      type: 'client',
      message: 'Hi, I am your support agent. How can I help you?',
    });
  }

  openSupportPopup() {
    this.isOpen = !this.isOpen;
  }

  sendMessage() {
    const sentMessage = this.chatForm.value.message!;
    console.log('sentMessage=' + sentMessage);
    this.loading = true;
    this.messages.push({
      type: 'user',
      message: sentMessage,
    });
    this.chatForm.reset();
    this.scrollToBottom();

    // Find the relevant document_id based on the sentMessage
    this.currentDocumentId = this.findDocumentId(sentMessage);
    console.log('this.currentDocumentId==' + this.currentDocumentId);

    // Fetch the generated questions from the backend
    this.fetchGeneratedQuestions(this.currentDocumentId);
  }

  findDocumentId(sentMessage: string): string {
    console.log('sentMessage--->' + sentMessage);
    // Iterate over the generatedQuestions array
    for (const generatedQuestion of this.generatedQuestions) {
      console.log('generatedQuestion--->' + generatedQuestion);
      const documentSample = generatedQuestion.document_sample.toLowerCase();
      if (documentSample.includes(sentMessage.toLowerCase())) {
        // Return the document_id if the sentMessage is found in the document_sample
        return generatedQuestion.document_id;
      }
    }
    // If no matching document_id is found, you can handle it accordingly
    return ''; // Return an appropriate default value or handle the case when no document_id is found
  }

  fetchGeneratedQuestions(documentId: string) {
    this.messageService.getGeneratedQuestions(documentId).subscribe(
      (response: any) => {
        this.loading = false;
        const questions = response.questions;

        // Iterate over the questions and add them to the messages array
        questions.forEach((question: string) => {
          this.messages.push({
            type: 'client',
            message: question,
          });
        });

        console.log('this.messages:', this.messages);
        this.scrollToBottom();
      },
      (error: any) => {
        console.error('Error fetching generated questions:', error);
        this.loading = false;
      }
    );
  }

  scrollToBottom() {
    setTimeout(() => {
      try {
        this.myScrollContainer.nativeElement.scrollTop =
          this.myScrollContainer.nativeElement.scrollHeight + 500;
      } catch (err) {}
    }, 150);
  }
}
<div id="helpdesk">
  <button id="helpdesk-popup-button" (click)="openSupportPopup()">
    Need Help?
  </button>
  <div id="helpdesk-popup" [style.display]="isOpen ? 'block' : 'none'">
    <div id="helpdesk-popup-header">
      Coding Nexus helpdesk
      <button id="helpdesk-popup-close-button" (click)="openSupportPopup()">
        Close
      </button>
    </div>
    <div id="helpdesk-popup-body">
      <div class="messages" #scrollMe>
        <div *ngFor="let message of messages" class="message">
          <div [class]="message.type">
            {{ message.message }}
          </div>
        </div>
        <div
          *ngIf="loading"
          class="message"
          style="width: 100%; display: block">
          <div [class]="'client'">...</div>
        </div>
      </div>
      <div class="generated-questions">
        <h3>Related Questions:</h3>
        <ul>
          <li *ngFor="let question of generatedQuestions[currentDocumentId]?.questions">
            {{ question }}
          </li>
        </ul>
      </div>
    </div>
    <form id="helpdesk-popup-footer" [formGroup]="chatForm">
      <input
        formControlName="message"
        type="text"
        id="helpdesk-popup-input"
        placeholder="Type your message here..."
      />
      <button
        id="helpdesk-popup-submit-button"
        [disabled]="!chatForm.valid"
        (click)="sendMessage()"
      >
        Send
      </button>
    </form>
  </div>
</div>
